mapper.select:
SELECT COUNT(*)
FROM TB_BOOKMARK
WHERE MEMBER_NO = #{memberNo}
  AND REVIEW_NO = #{reviewNo}

mapper.insert:
INSERT INTO TB_BOOKMARK (MEMBER_NO, REVIEW_NO, ENROLL_DATE)
SELECT #{memberNo}, #{reviewNo}, SYSDATE
FROM dual
WHERE NOT EXISTS (
    SELECT 1
    FROM TB_BOOKMARK
    WHERE MEMBER_NO = #{memberNo}
      AND REVIEW_NO = #{reviewNo}
)

mapper.delete:
DELETE FROM TB_BOOKMARK
WHERE MEMBER_NO = #{memberNo}
  AND REVIEW_NO = #{reviewNo}
