mapper.select:
SELECT COUNT(1)
FROM TB_COMMENT_LIKE
WHERE COMMENT_NO = #{commentNo}
  AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM TB_COMMENT_LIKE
WHERE MEMBER_NO = #{memberNo}
  AND COMMENT_NO = #{commentNo}

mapper.select:
SELECT COUNT(1)
FROM TB_COMMENT_LIKE
WHERE COMMENT_NO = #{commentNo}
